// Recebe a mensagem do chat
const question = $input.first().json.question;
const sessionId = $input.first().json.sessionId;
const userId = $input.first().json.userId;

// Aqui você pode integrar com sua IA preferida
// Exemplo: OpenAI, Claude, Gemini, etc.

// Por enquanto, retorna uma resposta simulada
const responses = [
  `Entendi sua pergunta: "${question}". Como posso ajudá-lo melhor?`,
  `Interessante! Sobre "${question}", posso fornecer mais informações.`,
  `Obrigado pela pergunta. Vou processar: "${question}"`,
  `Sua pergunta "${question}" foi recebida com sucesso!`
];

const randomResponse = responses[Math.floor(Math.random() * responses.length)];

return {
  answer: randomResponse,
  sessionId: sessionId,
  timestamp: new Date().toISOString(),
  originalQuestion: question
};
